INSERT INTO `company` (`id`, `imageId`, `name`, `phone`, `address`, `description`, `createDate`, `updateDate`) VALUES (1, 1, 'Zuhlke Engineering Vietnam', '0902 123 456', 'Số 10, đường Hoàng Diệu, phường Bến Nghé, quận 1, TP. Hồ Chí Minh', 'Công ty tự xây dựng chương trình “Great To Work” internal, nhân viên sẽ feedback ẩn danh để mọi người có thể feedback chân thật nhất, công ty rất coi trọng con người. Trang thiết bị được cung cấp đầy đủ: MacBook Pro, Apple keyboard,...', '2023-04-05', '2023-06-02');
INSERT INTO `company` (`id`, `imageId`, `name`, `phone`, `address`, `description`, `createDate`, `updateDate`) VALUES (2, 2, 'NFQ Asia', '0915 987 654', 'Tầng 5, tòa nhà Indochina Plaza Hanoi, số 241 Xuân Thủy, phường Dịch Vọng Hậu, quận Cầu Giấy, Hà Nội', 'Thời gian làm việc linh động, thoải mái, có thể làm ở office hoặc work from home thoải mái. Được làm việc thường xuyên với khách hàng châu Âu nên khả năng giao tiếp tiếng Anh của mình ngày càng có tiến bộ vượt bậc. Sếp và đồng nghiệp ai cũng thân thiện.', '2023-05-07', '2023-06-02');
INSERT INTO `company` (`id`, `imageId`, `name`, `phone`, `address`, `description`, `createDate`, `updateDate`) VALUES (3, 3, 'Orient Software Development Corp.', '0988 456 789', 'Số 128, đường Lê Lợi, phường Vĩnh Trại, thành phố Lạng Sơn, tỉnh Lạng Sơn', 'Môi trường làm việc năng động, mọi người hòa đồng, hỗ trợ nhiệt tình nhau. Thời gian làm việc linh hoạt. Có trang bị điểm tâm sáng cho nhân viên. Chiều nào cũng có ăn trái cây. Trà, cafe, bánh, kẹo phủ đầy pantry. Đa số dự án ko OT, nếu...\r\n', '2023-05-07', '2023-06-02');
INSERT INTO `company` (`id`, `imageId`, `name`, `phone`, `address`, `description`, `createDate`, `updateDate`) VALUES (4, 4, 'Coc Coc', '0973 321 654', 'Số 56, đường Lý Thường Kiệt, phường Tân An, thành phố Buôn Ma Thuột, tỉnh Đắk Lắk', 'Ở Cốc Cốc thì tuyệt vời nhất là con người, gần như không có khoảng cách giữa CEO, Leader và nhân viên cho tới các cô Maid ( ít công ty có các cô Maid riêng như ở đây ). Cụ thể thì tôi có thể gặp mặt và bắt chuyện với CEO, Leader của mình...\r\n.', '2023-05-07', '2023-06-02');
INSERT INTO `company` (`id`, `imageId`, `name`, `phone`, `address`, `description`, `createDate`, `updateDate`) VALUES (5, 5, 'Tyme', '0962 654 321', 'Số 123, đường Hùng Vương, phường Đoàn Kết, thành phố Phan Rang-Tháp Chàm, tỉnh Ninh Thuận', 'Công ty có chế độ lương và thưởng tốt, khá cảnh trạnh trên thị trường. Có review performance hàng năng cùng với nhiều chế độ đãi ngộ, chăm sóc sức khoẻ của nhân viên và gia đình (premium health care), nhiều ngày phép năm, off ngày sinh...\r\n', '2023-05-07', '2023-06-02');
INSERT INTO `company` (`id`, `imageId`, `name`, `phone`, `address`, `description`, `createDate`, `updateDate`) VALUES (6, 6, 'Niteco Vietnam Co., Ltd', '0947 987 654\r\n', 'Số 321, đường Nguyễn Công Trứ, phường An Thới, thành phố Rạch Giá, tỉnh Kiên Giang', 'Công việc: Các dự án của công ty đa dạng, mỗi dự án đều có những thách thức để học hỏi (quan trọng nhất là không OT nhiều). Đồng nghiệp: dễ mến và có chuyên môn giỏi nên môi trường làm việc vui vẻ không drama. Training: Làm ở Niteco..', '2023-05-22', '2023-06-02');
INSERT INTO `company` (`id`, `imageId`, `name`, `phone`, `address`, `description`, `createDate`, `updateDate`) VALUES (7, 7, 'Absolute Software (Vietnam) Ltd', '0936 456 789', 'Số 6, đường Trần Văn Điểm, phường 13, quận 5, TP. Hồ Chí Minh', 'Bạn bè thường hỏi thăm mình ở Absolute như thế nào? mình thường ví von, giống như nhà ít con vậy. Nếu bạn ở nhà đông con thì dù ba mẹ có thương thì sự chăm lo cũng có hạn, nhưng nhà ít con thì khác, tất cả các thành viên trong gia đình...\r\n', '2023-05-22', '2023-06-02');
