INSERT INTO `company` (`id`, `imageId`, `name`, `phone`, `address`, `description`, `createDate`, `updateDate`) VALUES (9, 9, 'Samsung Electronics HCMC CE Complex', '0898 654 321', 'Số 789, đường Trần Hưng Đạo, phường Hưng Đạo, thành phố Thái Bình, tỉnh Thái Bình', 'Samsung Electronics HCMC CE Complex', '2023-05-22', '2023-06-02');
INSERT INTO `company` (`id`, `imageId`, `name`, `phone`, `address`, `description`, `createDate`, `updateDate`) VALUES (10, 10, 'Teko Vietnam', '0887 987 654\r\n', 'Số 15, đường Nguyễn Văn Cừ, phường Xuân Phú, thành phố Tam Kỳ, tỉnh Quảng Nam', 'Môi trường làm việc: Mọi thứ đều tốt, văn phòng đẹp, đãi ngộ đầy đủ, bảo hiểm full lương + cho người thân, pantry luôn có đồ ăn sáng, ăn xế, hỗ trợ quay cơm trưa. Ngày phép nếu không dùng hết được giữ sang năm tiếp theo. Đồng nghiệp hoà...', '2023-05-22', '2023-06-02');
INSERT INTO `company` (`id`, `imageId`, `name`, `phone`, `address`, `description`, `createDate`, `updateDate`) VALUES (11, 11, 'Netcompany', '0876 456 789', 'Số 52, đường Hùng Vương, phường Trần Phú, thành phố Hà Giang, tỉnh Hà Giang', 'Văn phòng đẹp. Môi trường quốc tế, phần lớn làm việc chung với Poland và Denmark. Lương cao hơn so với mặt bằng chung. Có ăn trưa, friday bar nhậu nhẹt, đua xe Go-kart và tổ chức sinh nhật, ăn uống no nê, tăng cân vù vù. Có hỗ trợ học và...\r\n', '2023-05-22', '2023-06-02');
INSERT INTO `company` (`id`, `imageId`, `name`, `phone`, `address`, `description`, `createDate`, `updateDate`) VALUES (12, 12, 'Positive Thinking Company', '0865 321 654', 'Số 99, đường Trần Phú, phường Xuân Hà, thành phố Sơn La, tỉnh Sơn La', 'Văn phòng đẹp và được design theo tiêu chuẩn hiện đại hướng đến tính tiện nghi và hiệu suất cao trong công việc. Lương thưởng và phúc lợi tốt (như Hybrid working model và sẽ cung cấp laptop/ màn hình xịn). Nhân viên luôn được support...', '2023-05-22', '2023-06-02');
INSERT INTO `company` (`id`, `imageId`, `name`, `phone`, `address`, `description`, `createDate`, `updateDate`) VALUES (13, 13, 'LG Electronics Development Vietnam (LGEDV)', '0854 654 321', 'Số 77, đường Lê Lai, phường Phan Chu Trinh, thành phố Quy Nhơn, tỉnh Bình Định', 'Các benefit khá ổn. Công việc ổn định, làm việc với KH nước ngoài (Mỹ, Đức, Hàn, Trung). Sếp thoải mái, tâm lý. Có budget ăn uống, chơi bời hàng tháng. Event cũng khá hay ho (Mens day, Thi ăn kem, Xem phim). Được tính lương OT theo...', '2023-05-22', '2023-06-02');
INSERT INTO `company` (`id`, `imageId`, `name`, `phone`, `address`, `description`, `createDate`, `updateDate`) VALUES (14, 14, 'KMS Technology', '0843 987 654', 'Số 210, đường Lê Hồng Phong, phường Tào Xuyên, thành phố Nha Trang, tỉnhKhánh Hòa', 'Các chính sách của công ty hỗ trợ rất nhiều cho nhân viên, giờ giấc làm việc thỏa mái, hỗ trợ chi phí học tập trên các nền tảng udemy, quan tâm tới nhân viên thông qua các event, lễ, tết, ... Dự án đa dạng trên nhiều lĩnh vực, áp dụng...', '2023-05-22', '2023-06-02');
INSERT INTO `company` (`id`, `imageId`, `name`, `phone`, `address`, `description`, `createDate`, `updateDate`) VALUES (15, 15, 'NashTech', '0843 987 654', 'Số 43, đường Hoàng Văn Thụ, phường Vĩnh Trung, thành phố Đà Nẵng, tỉnh Đà Nẵng', 'Văn phòng đẹp, môi trường tốt, được sự giúp đỡ nhiệt tình từ các anh/chị mentor cũng như cấp trên. CÓ định hướng phát triển nghề nghiệp rõ ràng, có các khóa nâng cao skill bản thân. OT được trả lương đầy đủ, nhưng mà hầu như không có OT', '2023-05-22', '2023-06-02');
